package com.f1.base;import java.lang.annotation.ElementType;import java.lang.annotation.Inherited;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** *  * Indicates that the method should be available for the F1 CLI(command line interface). This is used to decorate classes (and provide help) in addition to decorating methods. *  *  *  * @author rcooke *  *  *  */@Inherited@Target({ ElementType.METHOD, ElementType.TYPE })@Retention(RetentionPolicy.RUNTIME)public @interface Console {	/**	 * 	 * @return generate help available to the user	 * 	 */	public String name() default "";	/**	 * 	 * @return generate help available to the user	 * 	 */	public String help() default "";	/**	 * 	 * @return help about the parameters	 * 	 */	public String[] params() default {};	/**	 * 	 * Only applicable for classes.	 * 	 * 	 * 	 * @return should methods w/o this annotation be available	 * 	 */	public boolean hideUndeclared() default true;}