package com.f1.console.impl;import com.f1.console.ConsoleEvent;public class BasicConsoleEvent implements ConsoleEvent {	private final byte type;	private final long time;	private final String connectionDescription;	private final String text;	private final String username;	public BasicConsoleEvent(byte type, long time, String connectionDescription, String text, String username) {		this.type = type;		this.time = time;		this.connectionDescription = connectionDescription;		this.text = text;		this.username = username;	}	@Override	public String getText() {		return text;	}	@Override	public long getTime() {		return time;	}	@Override	public String getConnectionDescription() {		return connectionDescription;	}	@Override	public byte getType() {		return type;	}	@Override	public String toString() {		return getClass().getSimpleName() + ": " + text;	}	@Override	public String getUsername() {		return username;	}}