package com.f1.console.impl;import java.util.regex.Pattern;import com.f1.console.ConsoleManager;import com.f1.console.ConsoleSession;import com.f1.utils.EnvironmentDump;import com.f1.utils.PropertyController;public class ShowEnvConsoleService extends AbstractConsoleService {	public ShowEnvConsoleService() {		super("SHOW ENV", "SHOW +ENV", "displays environment overview");	}	private static final Pattern PATTERN = Pattern.compile("SHOW ENV", Pattern.CASE_INSENSITIVE);	@Override	public void doRequest(ConsoleSession session, String[] options) {		ConsoleManager manager = session.getManager();		Class startupClass = (Class) manager.getValue("startupClass");		String[] args = (String[]) manager.getValue("startupArgs");		PropertyController properties = (PropertyController) manager.getValue("startupProperties");		StringBuilder sb = new StringBuilder();		EnvironmentDump.dump(startupClass, args, properties, sb);		session.getConnection().println(sb.toString());	}	static public void init(ConsoleManager manager, Class startupClass, String[] startupArgs, PropertyController propertyController) {		manager.setValue("startupClass", startupClass);		manager.setValue("startupArgs", startupArgs);		manager.setValue("startupProperties", propertyController);	}}