package com.f1.fixclientsim;import java.awt.Rectangle;import java.util.logging.Logger;import javax.swing.JComboBox;import javax.swing.JComponent;import javax.swing.JOptionPane;import javax.swing.JRadioButton;import javax.swing.JTextField;import javax.swing.JToggleButton;import javax.swing.text.JTextComponent;import com.f1.base.Legible;import com.f1.utils.LH;import com.f1.utils.SH;import com.f1.utils.casters.Caster_Boolean;public class SwingHelper {	private static final Logger log = Logger.getLogger(SwingHelper.class.getName());	public static Rectangle center(Rectangle toCenter, Rectangle centerOn) {		double w = centerOn.getWidth() - toCenter.getWidth();		double h = centerOn.getHeight() - toCenter.getHeight();		toCenter.setLocation(centerOn.x + (int) w / 2, centerOn.y + (int) h / 2);		return toCenter;	}	static public Object getValue(JComponent component) {		if (component instanceof JTextField) {			return ((JTextComponent) component).getText();		} else if (component instanceof JComboBox) {			return ((JComboBox) component).getSelectedItem();		} else if (component instanceof JToggleButton) {			return ((JRadioButton) component).isSelected();		} else			throw new RuntimeException("unknown type: " + component.getClass().getName());	}	public static void setValue(JComponent component, Object value) {		if (component instanceof JTextField) {			((JTextComponent) component).setText(SH.noNull(value));		} else if (component instanceof JComboBox) {			((JComboBox) component).setSelectedItem(value);		} else if (component instanceof JToggleButton) {			((JRadioButton) component).setSelected(Caster_Boolean.INSTANCE.cast(value));		} else			throw new RuntimeException("unknown type: " + component.getClass().getName());	}	public static void showDialog(Exception e) {		LH.info(log, "Exception message shown in dialog", e);		String text;		if (e instanceof Legible) {			text = ((Legible) e).toLegibleString();		} else {			text = e.getMessage();		}		if (SH.isnt(text))			text = "Exception: " + e.getClass().getName();		JOptionPane.showMessageDialog(null, text, "error", JOptionPane.ERROR_MESSAGE);	}}