package com.f1.utils.structs;import com.f1.base.Factory;import com.f1.utils.BasicPointer;public class TupleFactory implements Factory<Integer, Tuple> {	public final static TupleFactory INSTANCE = new TupleFactory();	public Tuple newTuple(int size) {		switch (size) {			case 0:				throw new IndexOutOfBoundsException("0");			case 1:				return new BasicPointer<Object>();			case 2:				return new Tuple2<Object, Object>();			case 3:				return new Tuple3<Object, Object, Object>();			default:				return new TupleN(size);		}	}	@Override	public Tuple get(Integer size) {		return newTuple(size);	}}