package com.f1.utils.assist;import static org.junit.Assert.assertEquals;import java.util.LinkedHashMap;import java.util.Map;import org.junit.Test;import com.f1.utils.CH;import com.f1.utils.SH;import com.f1.utils.VH;public class RootAssisterTests {	@Test	public void testToLegibleString() {		Map<Object, Object> m = CH.m(new LinkedHashMap(), "this", 1, "that", 2, "those", 2);		Map<Object, Object> m2 = CH.m("this", 1, "that", 2);		m.put("here", m2);		m2.put("here", m);		String s = RootAssister.INSTANCE.toLegibleString(m, 10000);		String s2 = "LinkedHashMap{\"this\"=1,\"that\"=2,\"those\"=2,\"here\"=HashMap{\"this\"=1,\"here\"=<circ-ref>,\"that\"=2,},}";		s = s.replaceAll(" ", "");		s = SH.replaceAll(s, '\n', "");		s = SH.replaceAll(s, '\r', "");		assertEquals(s2, s);	}	@Test	public void testPrimitiveArray() {		System.out.println(VH.toLegibleString("this".getBytes(), 10000));	}}